package app.componentsimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.RowScopeimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.ArrowBackimport androidx.compose.material3.Iconimport androidx.compose.material3.IconButtonimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport app.utils.theme.ExtraLargeSpacingimport app.utils.theme.MediumSpacingimport app.utils.theme.SmallSpacingimport cafe.adriel.voyager.navigator.LocalNavigatorimport cafe.adriel.voyager.navigator.currentOrThrow@Composablefun CustomAppBar(	modifier: Modifier = Modifier,	title: @Composable () -> Unit,	actions: @Composable RowScope.() -> Unit,	canGoBack: Boolean = false) {	val navigator = LocalNavigator.currentOrThrow	Row(		modifier = modifier			.fillMaxWidth()			.padding(				horizontal = MediumSpacing,				vertical = SmallSpacing			),		verticalAlignment = Alignment.CenterVertically,		horizontalArrangement = Arrangement.SpaceBetween	) {		if (canGoBack) {			IconButton(				onClick = {					navigator.pop()				}			) {				Icon(					imageVector = Icons.Default.ArrowBack,					contentDescription = null				)			}		}		title()		Row(			modifier = modifier,			horizontalArrangement = Arrangement.spacedBy(ExtraLargeSpacing),			verticalAlignment = Alignment.CenterVertically,			content = actions		)	}}