package app.componentsimport androidx.compose.material3.Buttonimport androidx.compose.material3.ButtonDefaultsimport androidx.compose.material3.CircularProgressIndicatorimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dp@Composablefun CustomButton(	modifier: Modifier = Modifier,	text: String,	onClick: () -> Unit,	enabled: Boolean,) {	Button(		onClick = { if (enabled) onClick() },		modifier = modifier,		elevation = ButtonDefaults.buttonElevation(			defaultElevation = 0.dp		),		shape = MaterialTheme.shapes.medium,		enabled = enabled	) {		if (enabled) {			Text(text = text)		} else {			CircularProgressIndicator(				color = MaterialTheme.colorScheme.onPrimary,				strokeWidth = 1.5.dp			)		}	}}