package app.createProfileimport androidx.compose.runtime.Composableimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport app.main.MainScreenimport cafe.adriel.voyager.core.screen.Screenimport cafe.adriel.voyager.koin.getScreenModelimport cafe.adriel.voyager.navigator.LocalNavigatorimport cafe.adriel.voyager.navigator.currentOrThrowobject CreateProfileScreen : Screen {	@Composable	override fun Content() {		val navigator = LocalNavigator.currentOrThrow		val screenModel = getScreenModel<CreateProfileScreenModel>()		val state by screenModel.state.collectAsState()		CreateProfileContent(			state = state,			onAvatarChange = screenModel::updateAvatar,			onUsernameChange = screenModel::updateUsername,			onGenderChange = screenModel::updateGender,			onPhoneChange = screenModel::updatePhone,			onSubmitClick = screenModel::createProfile,			onNavigateToHome = { navigator.replaceAll(MainScreen) }		)	}}