package app.loginimport androidx.compose.material3.SnackbarHostStateimport androidx.compose.runtime.Composableimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.runtime.rememberimport app.main.MainScreenimport app.register.RegisterScreenimport cafe.adriel.voyager.core.screen.Screenimport cafe.adriel.voyager.koin.getScreenModelimport cafe.adriel.voyager.navigator.LocalNavigatorimport cafe.adriel.voyager.navigator.currentOrThrowobject LoginScreen : Screen {	@Composable	override fun Content() {		val snackbarHostState = remember { SnackbarHostState() }		val navigator = LocalNavigator.currentOrThrow		val screenModel = getScreenModel<LoginScreenModel>()		val state by screenModel.state.collectAsState()		LoginContent(			snackbarHostState = snackbarHostState,			state = state,			onEmailChange = screenModel::updateEmail,			onPasswordChange = screenModel::updatePassword,			onLoginClick = screenModel::login,			onNavigateToRegister = { navigator.replace(RegisterScreen) },			onNavigateToHome = { navigator.replaceAll(MainScreen) }		)	}}