package app.loginimport androidx.compose.material3.SnackbarHostStateimport androidx.compose.runtime.Composableimport androidx.compose.runtime.remember@Composablefun LoginScreen() {	val snackbarHostState = remember { SnackbarHostState() }	LoginContent(		snackbarHostState = snackbarHostState,		state = LoginState(),		onEmailChange = {},		onPasswordChange = {},		onLoginClick = {},		onNavigateToRegister = {},		onNavigateToHome = {}	)}