package app.login.componentsimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Rowimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport app.utils.theme.SmallSpacing@Composablefun GoToRegister(	modifier: Modifier = Modifier,	onNavigateToRegister: () -> Unit,	enable: Boolean) {	Row(		modifier = modifier,		horizontalArrangement = Arrangement.spacedBy(SmallSpacing)	) {		Text(text = "Don't have an account?", style = MaterialTheme.typography.labelSmall)		Text(			text = "Sign up",			style = MaterialTheme.typography.labelSmall,			color = MaterialTheme.colorScheme.primary,			modifier = modifier.clickable {				if (enable) onNavigateToRegister()			}		)	}}