package app.mainimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.material3.Scaffoldimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport app.main.components.CustomBottomBarimport app.main.home.HomeTabimport cafe.adriel.voyager.navigator.tab.CurrentTabimport cafe.adriel.voyager.navigator.tab.TabNavigator@Composablefun MainContent(	modifier: Modifier = Modifier) {	TabNavigator(HomeTab) {		Scaffold(			modifier = modifier				.fillMaxSize(),			bottomBar = {				CustomBottomBar(modifier)			}		) {			CurrentTab()		}	}}