package app.main.componentsimport androidx.compose.foundation.layout.RowScopeimport androidx.compose.material3.Iconimport androidx.compose.material3.NavigationBarimport androidx.compose.material3.NavigationBarItemimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport app.main.home.HomeTabimport app.main.post.PostTabimport app.main.profile.ProfileTabimport app.main.notification.NotificationTabimport app.main.search.SearchTabimport cafe.adriel.voyager.navigator.tab.LocalTabNavigatorimport cafe.adriel.voyager.navigator.tab.Tab@Composablefun CustomBottomBar(	modifier: Modifier = Modifier) {	NavigationBar(modifier) {		TabNavigationItem(HomeTab)		TabNavigationItem(SearchTab)		TabNavigationItem(PostTab)		TabNavigationItem(NotificationTab)		TabNavigationItem(ProfileTab)	}}@Composableprivate fun RowScope.TabNavigationItem(tab: Tab) {	val tabNavigator = LocalTabNavigator.current	NavigationBarItem(		selected = tabNavigator.current == tab,		onClick = { tabNavigator.current = tab },		icon = {			Icon(				painter = tab.options.icon!!,				contentDescription = tab.options.title			)		}	)}