package app.main.notificationimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.Favoriteimport androidx.compose.runtime.Composableimport androidx.compose.runtime.rememberimport androidx.compose.ui.graphics.vector.rememberVectorPainterimport cafe.adriel.voyager.navigator.tab.Tabimport cafe.adriel.voyager.navigator.tab.TabOptionsobject NotificationTab : Tab {	@Composable	override fun Content() {		NotificationContent()	}	override val options: TabOptions		@Composable		get() {			val title = "Notification"			val icon = rememberVectorPainter(				Icons.Default.Favorite			)			return remember {				TabOptions(					index = 3u,					title = title,					icon = icon				)			}		}}