package app.main.profileimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.isSystemInDarkThemeimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.rememberScrollStateimport androidx.compose.foundation.verticalScrollimport androidx.compose.material3.MaterialThemeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport app.main.profile.components.ProfileAppBarimport app.main.profile.components.ProfileInfoimport app.utils.theme.LargeSpacingimport app.utils.theme.SmallSpacing@Composablefun ProfileContent(	modifier: Modifier = Modifier,	state: ProfileState) {	Column(		modifier = modifier			.fillMaxSize()			.verticalScroll(rememberScrollState())			.background(				color = if (isSystemInDarkTheme()) {					MaterialTheme.colorScheme.background				} else {					MaterialTheme.colorScheme.surface				}			)			.padding(				horizontal = SmallSpacing,				vertical = LargeSpacing			),		horizontalAlignment = Alignment.CenterHorizontally	) {		ProfileAppBar(			modifier = modifier,			username = state.username		)		ProfileInfo(			modifier = modifier,			posts = state.posts,			followers = state.followers,			following = state.following		)	}}