package app.main.profile.componentsimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport app.utils.theme.LargeSpacingimport app.utils.theme.SmallSpacing@Composablefun ProfileInfo(	modifier: Modifier = Modifier,	posts: Int,	followers: Int,	following: Int) {	Row(		modifier = modifier			.fillMaxWidth()			.padding(				horizontal = SmallSpacing,				vertical = LargeSpacing			),		verticalAlignment = Alignment.CenterVertically,		horizontalArrangement = Arrangement.SpaceAround	) {		Column(			modifier = modifier,			horizontalAlignment = Alignment.CenterHorizontally,			verticalArrangement = Arrangement.spacedBy(SmallSpacing)		) {			Text(				text = posts.toString(),				style = MaterialTheme.typography.bodyMedium			)			Text(				text = "Posts",				style = MaterialTheme.typography.bodySmall			)		}		Column(			modifier = modifier,			horizontalAlignment = Alignment.CenterHorizontally,			verticalArrangement = Arrangement.spacedBy(SmallSpacing)		) {			Text(				text = followers.toString(),				style = MaterialTheme.typography.bodyMedium			)			Text(				text = "Followers",				style = MaterialTheme.typography.bodySmall			)		}		Column(			modifier = modifier,			horizontalAlignment = Alignment.CenterHorizontally,			verticalArrangement = Arrangement.spacedBy(SmallSpacing)		) {			Text(				text = following.toString(),				style = MaterialTheme.typography.bodyMedium			)			Text(				text = "Following",				style = MaterialTheme.typography.bodySmall			)		}	}}