package app.registerimport androidx.compose.material3.SnackbarHostStateimport androidx.compose.runtime.Composableimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.runtime.rememberimport app.login.LoginScreenimport cafe.adriel.voyager.core.screen.Screenimport cafe.adriel.voyager.koin.getScreenModelimport cafe.adriel.voyager.navigator.LocalNavigatorimport cafe.adriel.voyager.navigator.currentOrThrowobject RegisterScreen : Screen {	@Composable	override fun Content() {		val snackbarHostState = remember { SnackbarHostState() }		val navigator = LocalNavigator.currentOrThrow		val screenModel = getScreenModel<RegisterScreenModel>()		val state by screenModel.state.collectAsState()		RegisterContent(			snackbarHostState = snackbarHostState,			state = state,			onEmailChange = screenModel::updateEmail,			onPasswordChange = screenModel::updatePassword,			onConfirmPasswordChange = screenModel::updateConfirmPassword,			onRegisterClick = screenModel::register,			onNavigateToLogin = { navigator.replace(LoginScreen) },			onNavigateToCreateProfile = {}		)	}}