package app.register.componentsimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Rowimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport app.utils.theme.SmallSpacing@Composablefun GoToLogin(	modifier: Modifier = Modifier,	onNavigateToLogin: () -> Unit,	enable: Boolean) {	Row(		modifier = modifier,		horizontalArrangement = Arrangement.spacedBy(SmallSpacing)	) {		Text(text = "Already have an account?", style = MaterialTheme.typography.labelSmall)		Text(			text = "Sign in",			style = MaterialTheme.typography.labelSmall,			color = MaterialTheme.colorScheme.primary,			modifier = modifier.clickable {				if (enable) onNavigateToLogin()			}		)	}}