package features.auth.diimport features.auth.domain.repository.AuthRepositoryimport features.auth.domain.useCases.GetCurrentUidUseCaseimport features.auth.domain.useCases.LoginUseCaseimport features.auth.domain.useCases.RegisterUseCaseimport features.auth.infrastructure.dataSource.AuthRemoteDataSourceimport features.auth.infrastructure.repository.AuthRepositoryImplimport org.koin.dsl.moduleval authModule = module {	single<AuthRepository> {		AuthRepositoryImpl(get(), get())	}	single {		AuthRemoteDataSource(get())	}	single {		LoginUseCase(get())	}	single {		RegisterUseCase(get())	}	single {		GetCurrentUidUseCase(get())	}}