package features.auth.infrastructure.dataSourceimport com.rickclephas.kmp.nativecoroutines.NativeCoroutinesimport dev.gitlive.firebase.auth.FirebaseAuthimport features.auth.domain.model.AuthRequestclass AuthRemoteDataSource(	private val auth: FirebaseAuth) {	@NativeCoroutines	suspend fun login(request: AuthRequest) {		auth.signInWithEmailAndPassword(			email = request.email,			password = request.password		)	}	@NativeCoroutines	suspend fun register(request: AuthRequest) {		auth.createUserWithEmailAndPassword(			email = request.email,			password = request.password		)	}	fun getCurrentUid(): String {		return auth.currentUser?.uid!!	}}