package features.auth.infrastructure.repositoryimport com.rickclephas.kmp.nativecoroutines.NativeCoroutinesimport features.auth.domain.model.AuthRequestimport features.auth.domain.repository.AuthRepositoryimport features.auth.infrastructure.dataSource.AuthRemoteDataSourceimport features.utils.DispatcherProviderimport kotlinx.coroutines.withContextclass AuthRepositoryImpl(	private val dispatcher: DispatcherProvider,	private val remoteDataSource: AuthRemoteDataSource) : AuthRepository {	@NativeCoroutines	override suspend fun login(request: AuthRequest) {		withContext(dispatcher.io) {			remoteDataSource.login(request)		}	}}