package features.profile.domain.useCasesimport com.rickclephas.kmp.nativecoroutines.NativeCoroutinesimport features.profile.domain.model.ProfileModelimport features.profile.domain.repository.ProfileRepositoryclass CreateProfileUseCase(	private val repository: ProfileRepository) {	@NativeCoroutines	suspend operator fun invoke(profile: ProfileModel) =		repository.createProfile(profile)}