package features.profile.infrastructure.dataSourceimport com.rickclephas.kmp.nativecoroutines.NativeCoroutinesimport dev.gitlive.firebase.firestore.FirebaseFirestoreimport features.profile.domain.model.ProfileModelclass ProfileRemoteDataSource(	private val db: FirebaseFirestore) {	@NativeCoroutines	suspend fun getProfile(uid: String): ProfileModel {		return db			.collection("profile")			.document(uid)			.get()			.data()	}}